using System;
using System.Collections.Generic;
using System.Text;

namespace LeetCode
{
    class _76
    {
        public void runCode()
        {
            string s = "";
            string t = "";
            //string s = "ADOBECODEBANC";
            //string t = "ABC";
            //string s = "a";
            //string t = "a";
            MinWindow(s, t);
        }
        public string MinWindow(string s, string t)
        {
            List<int> list = new List<int>();

            int min = s.Length;
            string res="";
            for (int i = 0; i < s.Length; i++)
            {
                if (t.Contains(s[i]))
                {
                    list.Add(i);
                }

            }

            

            list.Sort();


            int l,r;
            int indexL = 0, indexR = 0;
            while (indexL <= indexR && indexR < list.Count)
            {
                l = list[indexL];
                r = list[indexR];
                string subStr = s.Substring(l, r - l + 1);

                int i = 0;
                while(i < t.Length)
                {
                    if (!subStr.Contains(t[i]))
                    {

                        break;
                    }
                    else
                    {
                        
                        subStr = subStr.Remove(subStr.IndexOf(t[i]),1);
                        i++;
                    }

                }

                if(i == t.Length)
                {
                    if (min >= r - l + 1)
                    {
                        min = Math.Min(min, r - l + 1);
                        res = s.Substring(l, r - l + 1);
                    }
                        

                    ++indexL;
                }
                else
                {
                    
                    ++indexR;
                }
            }

            return res;
        }
    

}
}
